package com.iebm.ssm.appModules;import com.iebm.ssm.util.Constant;import org.openqa.selenium.WebDriver;import com.iebm.ssm.pageObjects.LoginPage;/* *TODO *LC *下午6:18:06 */public class Login_Action {    private static LoginPage loginPage = new LoginPage(Constant.driver);    public static void execute(String userName,String password) throws Exception{        loginPage.userName().sendKeys(userName);        loginPage.password().sendKeys(password);		loginPage.validateCode().sendKeys("1111");        loginPage.loginbutton().click();    }    public static void loginOutexecute() throws Exception{        loginPage.loginOutbutton().click();        loginPage.loginOutOKbutton().click();    }}