package com.iebm.ssm.pageObjects;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import com.iebm.ssm.util.ObjectMap;/* *TODO *Administrator *下午1:13:23 */public class LoginPage {    private WebElement element = null;    private ObjectMap  objectmap = new ObjectMap("./resource/loginPageMap.properties");    private WebDriver driver;    public LoginPage(WebDriver driver){        this.driver = driver;    }    public WebElement userName() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.loginId"));        return element;    }    public WebElement password() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.password"));        return element;    }    public WebElement validateCode() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.validateCode"));        return element;    }    public WebElement loginbutton() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.loginbutton"));        return element;    }}