package com.iebm.ssm.pageObjects;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import com.iebm.ssm.util.ObjectMap;import java.util.List;/* *TODO *Administrator *下午1:13:23 */public class LoginPage {    private WebElement element = null;    private ObjectMap  objectmap = new ObjectMap("loginPageMap.properties");    private WebDriver driver;    public LoginPage(WebDriver driver){        this.driver = driver;    }    public WebElement userName() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.loginId"));        return element;    }    public WebElement password() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.password"));        return element;    }    public WebElement validateCode() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.validateCode"));        return element;    }    public WebElement loginbutton() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.loginPage.loginbutton"));        return element;    }    public WebElement loginOutbutton() throws Exception {        element = driver.findElement(objectmap.getLocator("ssm.homePge.loginOutbutton"));        return element;    }    public WebElement loginOutOKbutton() throws Exception {        List<WebElement> elements = driver.findElements(objectmap.getLocator("ssm.homePge.loginOutOKbutton"));        return elements.get(0);    }    public WebElement loginOutnoOKbutton() throws Exception {        List<WebElement> elements = driver.findElements(objectmap.getLocator("ssm.homePge.loginOutOKbutton"));        return elements.get(1);    }}